import React from 'react';

const methods = [
  { id: 'arithmetic', name: 'Arithmetic Mean' },
  { id: 'normal-ratio', name: 'Normal Ratio' },
  { id: 'idw', name: 'Inverse Distance Weighting' },
  { id: 'isohyetal', name: 'Isohyetal Method' },
  { id: 'theissen', name: 'Theissen Polygon Method' },
];

const MethodSelector = ({ 
  selectedMethod, 
  onMethodChange,
  normalPrecipitation,
  onNormalPrecipitationChange
}) => {
  return (
    <div className="bg-white p-4 rounded-lg shadow">
      <h2 className="text-xl font-bold mb-4">Select Method</h2>
      <select
        value={selectedMethod}
        onChange={(e) => onMethodChange(e.target.value)}
        className="w-full p-2 border rounded"
      >
        <option value="">Select a method...</option>
        {methods.map((method) => (
          <option key={method.id} value={method.id}>
            {method.name}
          </option>
        ))}
      </select>

      {selectedMethod === 'normal-ratio' && (
        <div className="mt-4">
          <label className="block mb-2">Normal Precipitation (mm)</label>
          <input
            type="number"
            className="w-full p-2 border rounded"
            placeholder="Enter normal precipitation"
            value={normalPrecipitation}
            onChange={(e) => onNormalPrecipitationChange(e.target.value)}
            step="any"
            required
          />
        </div>
      )}

      {/* Method descriptions */}
      <div className="mt-4 text-sm text-gray-600">
        {selectedMethod === 'arithmetic' && (
          <p>Simple average of all station precipitation values.</p>
        )}
        {selectedMethod === 'normal-ratio' && (
          <p>Weighted average based on normal precipitation values.</p>
        )}
        {selectedMethod === 'idw' && (
          <p>Interpolation based on inverse distance weighting.</p>
        )}
        {selectedMethod === 'isohyetal' && (
          <p>Area-weighted average using precipitation contours.</p>
        )}
        {selectedMethod === 'theissen' && (
          <p>Area-weighted average using Thiessen polygons.</p>
        )}
      </div>
    </div>
  );
};

export default MethodSelector;
