import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import { useState, useEffect } from 'react';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix for default marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

function MapPage() {
  const [stations, setStations] = useState([]);
  const defaultPosition = [20.5937, 78.9629]; // Center of India

  const handleMapClick = (e) => {
    const newStation = {
      id: Date.now(),
      position: [e.latlng.lat, e.latlng.lng],
      precipitation: 0,
    };
    setStations([...stations, newStation]);
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-4xl font-bold mb-6">Interactive Map</h1>
      <div className="bg-white p-6 rounded-lg shadow-md">
        <div className="h-[600px] w-full">
          <MapContainer
            center={defaultPosition}
            zoom={5}
            style={{ height: '100%', width: '100%' }}
            onClick={handleMapClick}
          >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            {stations.map((station) => (
              <Marker key={station.id} position={station.position}>
                <Popup>
                  <div>
                    <p>Latitude: {station.position[0].toFixed(4)}</p>
                    <p>Longitude: {station.position[1].toFixed(4)}</p>
                    <input
                      type="number"
                      className="mt-2 p-1 border rounded"
                      value={station.precipitation}
                      onChange={(e) => {
                        const updatedStations = stations.map((s) =>
                          s.id === station.id
                            ? { ...s, precipitation: parseFloat(e.target.value) }
                            : s
                        );
                        setStations(updatedStations);
                      }}
                      placeholder="Precipitation (mm)"
                    />
                  </div>
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        </div>
      </div>
    </div>
  );
}

export default MapPage;
