import React, { useState, useEffect } from 'react';
import DataPanel from './components/DataPanel';
import MethodSelector from './components/MethodSelector';
import ResultsSection from './components/ResultsSection';
import { calculateArithmeticMean, calculateNormalRatio, calculateIDW } from './utils/calculations';

// Lazy load MapComponent to avoid SSR issues
const MapComponent = React.lazy(() => import('./components/MapComponent'));

function App() {
  const [stations, setStations] = useState([]);
  const [selectedMethod, setSelectedMethod] = useState('');
  const [results, setResults] = useState(null);
  const [normalPrecipitation, setNormalPrecipitation] = useState('');
  const [isClient, setIsClient] = useState(false);

  useEffect(() => {
    setIsClient(true);
  }, []);

  const handleDataUpload = (data) => {
    // Process CSV data
    const processedData = data.slice(1).map((row, index) => ({
      name: `Station ${index + 1}`,
      lat: parseFloat(row[0]),
      lng: parseFloat(row[1]),
      precipitation: parseFloat(row[2])
    }));
    setStations(processedData);
  };

  const handleManualAdd = (data) => {
    const newStation = {
      name: `Station ${stations.length + 1}`,
      lat: parseFloat(data.latitude),
      lng: parseFloat(data.longitude),
      precipitation: parseFloat(data.precipitation)
    };
    setStations([...stations, newStation]);
  };

  const calculateResults = () => {
    if (stations.length === 0 || !selectedMethod) {
      alert('Please add stations and select a method');
      return;
    }

    let calculatedValue;
    switch (selectedMethod) {
      case 'arithmetic':
        calculatedValue = calculateArithmeticMean(stations);
        break;
      case 'normal-ratio':
        if (!normalPrecipitation) {
          alert('Please enter normal precipitation value');
          return;
        }
        calculatedValue = calculateNormalRatio(stations, parseFloat(normalPrecipitation));
        break;
      case 'idw':
        // For IDW, we'll use the center of all stations as the target point
        const targetLat = stations.reduce((sum, s) => sum + s.lat, 0) / stations.length;
        const targetLng = stations.reduce((sum, s) => sum + s.lng, 0) / stations.length;
        calculatedValue = calculateIDW(stations, targetLat, targetLng);
        break;
      default:
        calculatedValue = 0;
    }

    setResults({
      calculatedValue,
      stations,
      method: selectedMethod
    });
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto py-6 px-4">
          <h1 className="text-3xl font-bold text-gray-900">
            Precipitation Calculator
          </h1>
        </div>
      </header>

      <main className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <div className="space-y-6">
            <DataPanel
              onDataUpload={handleDataUpload}
              onManualAdd={handleManualAdd}
            />
            <MethodSelector
              selectedMethod={selectedMethod}
              onMethodChange={setSelectedMethod}
              normalPrecipitation={normalPrecipitation}
              onNormalPrecipitationChange={setNormalPrecipitation}
            />
            <button
              onClick={calculateResults}
              className="w-full bg-blue-500 text-white p-3 rounded-lg hover:bg-blue-600"
            >
              Calculate
            </button>
          </div>

          <div className="space-y-6">
            {isClient && (
              <React.Suspense fallback={<div className="h-[400px] w-full bg-gray-100 rounded-lg animate-pulse" />}>
                <MapComponent stations={stations} />
              </React.Suspense>
            )}
            <ResultsSection results={results} />
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
