import React from 'react';

const methods = [
  { id: 'arithmetic', name: 'Arithmetic Mean' },
  { id: 'normal-ratio', name: 'Normal Ratio' },
  { id: 'idw', name: 'Inverse Distance Weighting' },
  { id: 'isohyetal', name: 'Isohyetal Method' },
  { id: 'theissen', name: 'Theissen Polygon Method' },
];

const MethodSelector = ({ selectedMethod, onMethodChange }) => {
  return (
    <div className="bg-white p-4 rounded-lg shadow">
      <h2 className="text-xl font-bold mb-4">Select Method</h2>
      <select
        value={selectedMethod}
        onChange={(e) => onMethodChange(e.target.value)}
        className="w-full p-2 border rounded"
      >
        <option value="">Select a method...</option>
        {methods.map((method) => (
          <option key={method.id} value={method.id}>
            {method.name}
          </option>
        ))}
      </select>

      {/* Method-specific inputs will be added here based on selectedMethod */}
      {selectedMethod === 'normal-ratio' && (
        <div className="mt-4">
          <label className="block mb-2">Normal Precipitation (mm)</label>
          <input
            type="number"
            className="w-full p-2 border rounded"
            placeholder="Enter normal precipitation"
            step="any"
          />
        </div>
      )}
    </div>
  );
};

export default MethodSelector;
