import React, { useState } from 'react';
import DataPanel from './components/DataPanel';
import MethodSelector from './components/MethodSelector';
import ResultsSection from './components/ResultsSection';

function App() {
  const [stations, setStations] = useState([]);
  const [selectedMethod, setSelectedMethod] = useState('');
  const [results, setResults] = useState(null);

  const handleDataUpload = (data) => {
    // Process CSV data
    const processedData = data.slice(1).map((row, index) => ({
      name: `Station ${index + 1}`,
      lat: parseFloat(row[0]),
      lng: parseFloat(row[1]),
      precipitation: parseFloat(row[2])
    }));
    setStations(processedData);
  };

  const handleManualAdd = (data) => {
    const newStation = {
      name: `Station ${stations.length + 1}`,
      lat: parseFloat(data.latitude),
      lng: parseFloat(data.longitude),
      precipitation: parseFloat(data.precipitation)
    };
    setStations([...stations, newStation]);
  };

  const calculateResults = () => {
    if (stations.length === 0 || !selectedMethod) {
      alert('Please add stations and select a method');
      return;
    }

    let calculatedValue;
    switch (selectedMethod) {
      case 'arithmetic':
        calculatedValue = stations.reduce((sum, station) => 
          sum + station.precipitation, 0) / stations.length;
        break;
      // Add other methods here
      default:
        calculatedValue = 0;
    }

    setResults({
      calculatedValue,
      stations,
      method: selectedMethod
    });
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto py-6 px-4">
          <h1 className="text-3xl font-bold text-gray-900">
            Precipitation Calculator
          </h1>
        </div>
      </header>

      <main className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div className="space-y-6">
            <DataPanel
              onDataUpload={handleDataUpload}
              onManualAdd={handleManualAdd}
            />
            <MethodSelector
              selectedMethod={selectedMethod}
              onMethodChange={setSelectedMethod}
            />
            <button
              onClick={calculateResults}
              className="w-full bg-blue-500 text-white p-3 rounded-lg hover:bg-blue-600"
            >
              Calculate
            </button>
          </div>

          <div>
            <ResultsSection results={results} />
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
