import { useState } from 'react';

function MethodsPage() {
  const [selectedMethod, setSelectedMethod] = useState('');
  const [result, setResult] = useState(null);

  const methods = [
    {
      id: 'arithmetic',
      name: 'Arithmetic Mean',
      description: 'Simple average of precipitation values from all stations.',
    },
    {
      id: 'normal-ratio',
      name: 'Normal Ratio',
      description: 'Weighted average based on normal precipitation values.',
    },
    {
      id: 'idw',
      name: 'Inverse Distance Weighting',
      description: 'Weighted average based on distance from the missing station.',
    },
    {
      id: 'isohyetal',
      name: 'Isohyetal Method',
      description: 'Area-weighted average using lines of equal rainfall.',
    },
    {
      id: 'theissen',
      name: 'Theissen Polygon',
      description: 'Area-weighted average using Theissen polygons.',
    },
  ];

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-4xl font-bold mb-6">Precipitation Estimation Methods</h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="col-span-1 bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-2xl font-semibold mb-4">Select Method</h2>
          <div className="space-y-4">
            {methods.map((method) => (
              <div
                key={method.id}
                className={`p-4 rounded-lg cursor-pointer transition-colors ${
                  selectedMethod === method.id
                    ? 'bg-blue-100 border-2 border-blue-500'
                    : 'bg-gray-50 hover:bg-gray-100'
                }`}
                onClick={() => setSelectedMethod(method.id)}
              >
                <h3 className="font-semibold">{method.name}</h3>
                <p className="text-sm text-gray-600">{method.description}</p>
              </div>
            ))}
          </div>
        </div>

        <div className="col-span-2 bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-2xl font-semibold mb-4">Method Details</h2>
          {selectedMethod ? (
            <div>
              {/* Method-specific inputs and calculations will go here */}
              <p className="text-gray-600">
                Select stations on the map and input precipitation values to calculate using this method.
              </p>
            </div>
          ) : (
            <p className="text-gray-600">Select a method to view details and start calculation.</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default MethodsPage;
