import React, { useMemo, useEffect } from 'react';
import { Polyline } from 'react-leaflet';
import { contours } from 'd3-contour';
import { range, interpolateRainbow } from 'd3-scale';
import { useMap } from 'react-leaflet';
import L from 'leaflet';

const IsohyetalLayer = ({ stations, bounds, targetPoint }) => {
  const map = useMap();

  useEffect(() => {
    if (!stations || stations.length < 3 || !bounds) return;

    // Remove existing layer if any
    const existingLayer = map._layers && Object.values(map._layers).find(
      layer => layer.options && layer.options.className === 'isohyetal-layer'
    );
    if (existingLayer) {
      map.removeLayer(existingLayer);
    }

