      const maxPoint = map.latLngToLayerPoint([bounds.maxLat, bounds.maxLng]);
      const width = maxPoint.x - minPoint.x;
      const height = maxPoint.y - minPoint.y;
      const cellSize = 10;
      const cols = Math.ceil(width / cellSize);
      const rows = Math.ceil(height / cellSize);

      // Create grid of points for interpolation
      const grid = new Array(rows * cols).fill(null).map((_, i) => {
        const col = i % cols;
        const row = Math.floor(i / cols);
        return {
          x: minPoint.x + col * cellSize,
          y: minPoint.y + row * cellSize
        };
      });

      // Interpolate values using IDW
      const power = 2;
      grid.forEach(point => {
        let weightedSum = 0;
        let weightSum = 0;
        points.forEach(station => {
          const dx = station.x - point.x;
          const dy = station.y - point.y;
          const distance = Math.sqrt(dx * dx + dy * dy);
          const weight = 1 / Math.pow(distance, power);
          weightedSum += station.value * weight;
          weightSum += weight;
        });
        point.value = weightedSum / weightSum;
      });

      // Generate contours
      const contourGenerator = contours()
        .size([cols, rows])
        .thresholds([0, 20, 40, 60, 80, 100, 120, 140, 160, 180, 200]); // Fixed thresholds every 20mm

      const values = new Float64Array(grid.length);
      grid.forEach((point, i) => {
        values[i] = point.value;
      });

      const contourData = contourGenerator(values);

      // Find target point's contour
      let targetContourValue = null;
      if (targetPixel) {
        const targetGridX = Math.floor((targetPixel.x - minPoint.x) / cellSize);
        const targetGridY = Math.floor((targetPixel.y - minPoint.y) / cellSize);
        const targetIndex = targetGridY * cols + targetGridX;
        if (targetIndex >= 0 && targetIndex < grid.length) {
          targetContourValue = Math.floor(grid[targetIndex].value / 20) * 20;
        }
      }

      // Create layer group for all contours
      const layers = [];

      // Convert contour coordinates to LatLng and create polygons
      contourData.forEach(contour => {
        const isTargetContour = targetContourValue !== null && 
          Math.abs(contour.value - targetContourValue) < 10;

        contour.coordinates.forEach(polygon => {
          const latLngs = polygon[0].map(point => {
            const x = minPoint.x + point[0] * cellSize;
            const y = minPoint.y + point[1] * cellSize;
            return map.layerPointToLatLng([x, y]);
          });

          // Create polygon for each contour
          const layer = L.polygon(latLngs, {
            color: isTargetContour ? '#000' : '#666',
            weight: isTargetContour ? 3 : 1,
            fillColor: getColorForPrecipitation(contour.value),
            fillOpacity: isTargetContour ? 0.4 : 0.2,
            className: 'isohyetal-contour'
          });

          // Add popup with contour value
          layer.bindPopup(`
            <div>
              <h3 class="font-bold">Precipitation Level</h3>
              <p>${contour.value.toFixed(0)} mm</p>
              ${isTargetContour ? '<p class="font-bold text-green-600">Contains Target Point</p>' : ''}
            </div>
          `);

          layers.push(layer);
        });
      });

