        const updatedMinPoint = map.latLngToLayerPoint([bounds.minLat, bounds.minLng]);

        layers.forEach(layer => {
          const latLngs = layer.getLatLngs()[0].map(latLng => {
            const point = map.latLngToLayerPoint([latLng.lat, latLng.lng]);
            const x = (point.x - updatedMinPoint.x) / cellSize;
            const y = (point.y - updatedMinPoint.y) / cellSize;
