      console.error('Error creating isohyetal contours:', error);
    }
  }, [map, stations, bounds, targetPoint]);

  return null;
};

// Helper function to get color based on precipitation value
function getColorForPrecipitation(value) {
  // Use fixed colors instead of interpolation
  if (value < 50) return '#3B82F6';  // blue
  if (value < 100) return '#10B981'; // green
  if (value < 150) return '#F59E0B'; // yellow
  return '#EF4444';                  // red
}

export default IsohyetalLayer;
