import React, { useState } from 'react';

const DataPanel = ({ onDataUpload, onManualAdd }) => {
  const [manualData, setManualData] = useState({
    latitude: '',
    longitude: '',
    precipitation: '',
  });

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const csvData = e.target.result;
          // Parse CSV data
          const parsedData = csvData.split('\\n').map(row => row.split(','));
          onDataUpload(parsedData);
        } catch (error) {
          console.error('Error parsing CSV:', error);
          alert('Error parsing CSV file');
        }
      };
      reader.readAsText(file);
    }
  };

  const handleManualSubmit = (e) => {
    e.preventDefault();
    onManualAdd(manualData);
    setManualData({
      latitude: '',
      longitude: '',
      precipitation: '',
    });
  };

  return (
    <div className="bg-white p-4 rounded-lg shadow">
      <h2 className="text-xl font-bold mb-4">Data Input</h2>

      {/* File Upload Section */}
      <div className="mb-4">
        <h3 className="font-semibold mb-2">Upload CSV</h3>
        <input
          type="file"
          accept=".csv"
          onChange={handleFileUpload}
          className="w-full p-2 border rounded"
        />
      </div>

      {/* Manual Input Form */}
      <form onSubmit={handleManualSubmit}>
        <h3 className="font-semibold mb-2">Manual Input</h3>
        <div className="space-y-3">
          <input
            type="number"
            placeholder="Latitude"
            value={manualData.latitude}
            onChange={(e) => setManualData({...manualData, latitude: e.target.value})}
            className="w-full p-2 border rounded"
            step="any"
            required
          />
          <input
            type="number"
            placeholder="Longitude"
            value={manualData.longitude}
            onChange={(e) => setManualData({...manualData, longitude: e.target.value})}
            className="w-full p-2 border rounded"
            step="any"
            required
          />
          <input
            type="number"
            placeholder="Precipitation (mm)"
            value={manualData.precipitation}
            onChange={(e) => setManualData({...manualData, precipitation: e.target.value})}
            className="w-full p-2 border rounded"
            step="any"
            required
          />
          <button
            type="submit"
            className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
          >
            Add Station
          </button>
        </div>
      </form>
    </div>
  );
};

export default DataPanel;
