import React from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMapEvents } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix for default marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

// Component to handle map clicks
function MapClickHandler({ onMapClick }) {
  useMapEvents({
    click: (e) => {
      const { lat, lng } = e.latlng;
      onMapClick(lat, lng);
    },
  });
  return null;
}

const MapComponent = ({ stations, onLocationSelect }) => {
  // Calculate map center based on stations or use a default
  const center = stations.length > 0
    ? [
        stations.reduce((sum, station) => sum + station.lat, 0) / stations.length,
        stations.reduce((sum, station) => sum + station.lng, 0) / stations.length,
      ]
    : [20, 0]; // Default center

  if (typeof window === 'undefined') {
    return null; // Return null during server-side rendering
  }

  return (
    <div className="space-y-4">
      <div className="h-[400px] w-full rounded-lg overflow-hidden shadow">
        <MapContainer
          center={center}
          zoom={stations.length > 0 ? 8 : 2}
          style={{ height: '100%', width: '100%' }}
          key={stations.length} // Force re-render when stations change
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />
          <MapClickHandler onMapClick={onLocationSelect} />

          {/* Existing stations */}
          {stations.map((station, index) => (
            <Marker
              key={index}
              position={[station.lat, station.lng]}
            >
              <Popup>
                <div>
                  <h3 className="font-bold">{station.name}</h3>
                  <p>Precipitation: {station.precipitation} mm</p>
                  <p>Latitude: {station.lat.toFixed(6)}</p>
                  <p>Longitude: {station.lng.toFixed(6)}</p>
                </div>
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>
      <div className="bg-white p-4 rounded-lg shadow">
        <p className="text-sm text-gray-600">
          Click anywhere on the map to set the station location. The coordinates will appear in the input fields.
        </p>
      </div>
    </div>
  );
};

export default MapComponent;
